  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'terraform_elb',
	title: 'Terraform ELB Integration',
	sidebar_label: 'Terraform ELB Integration'
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'AWS ELB Sandwich with PANFW Deployed using terraform_version',
  id: 'aws-elb-sandwich-with-panfw-deployed-using-terraform_version',
