  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'pandevice_qs',
	title: 'Quickstart',
	sidebar_label: 'Quickstart'
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Installation',
  id: 'installation',
  children: []
}, {
  value: 'How to import',
  id: 'how-to-import',
  children: []
}, {
  value: 'A few examples',
  id: 'a-few-examples',
  children: []
}, {
  value: 'Create a subinterface and commit:',
  id: 'create-a-subinterface-and-commit',
  children: []
}, {
  value: 'Perform show system info',
  id: 'perform-show-system-info',
  children: []
}, {
  value: 'Connecting to PAN-OS 8.0 and higher',
  id: 'connecting-to-pan-os-80-and-higher',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p><a parentName="p" {...{
        "href": "https://github.com/PaloAltoNetworks/pandevice"
      }}><img parentName="a" {...{
          "src": "https://img.shields.io/badge/GitHub-Repo-brightgreen?style=for-the-badge&logo=github",
          "alt": "GitHub page"
        }}></img></a>{` `}<a parentName="p" {...{
        "href": "https://github.com/PaloAltoNetworks/pandevice"
      }}><img parentName="a" {...{
          "src": "https://img.shields.io/github/stars/PaloAltoNetworks/pandevice?style=for-the-badge",
          "alt": "GitHub stars"
        }}></img></a></p>
    <h2 {...{
      "id": "installation"
    }}>{`Installation`}</h2>
    <p>{`The easiest method to install pandevice is using pip:`}</p>
    <pre><code parentName="pre" {...{}}>{`pip install pandevice
`}</code></pre>
    <p>{`Or, if you have virtualenvwrapper installed:`}</p>
    <pre><code parentName="pre" {...{}}>{`mkvirtualenv pandevice
pip install pandevice
`}</code></pre>
    <p>{`Pip will install the `}<a parentName="p" {...{
        "href": "/docs/panpython_qs"
      }}>{`pan-python`}</a>{` library as a dependency.`}</p>
    <p>{`Upgrade to the latest version:`}</p>
    <pre><code parentName="pre" {...{}}>{`pip install --upgrade pandevice
`}</code></pre>
    <h2 {...{
      "id": "how-to-import"
    }}>{`How to import`}</h2>
    <p>{`To use Palo Alto Networks Device Framework in a project:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`import pandevice
`}</code></pre>
    <p>{`You can also be more specific about which modules you want to import:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`from pandevice import firewall
from pandevice import network
`}</code></pre>
    <h2 {...{
      "id": "a-few-examples"
    }}>{`A few examples`}</h2>
    <p>{`For configuration tasks, create a tree structure using the classes in
each module. Nodes hierarchy must follow the model in the `}<a parentName="p" {...{
        "href": "http://pandevice.readthedocs.io/en/latest/configtree.html"
      }}>{`Configuration
Tree`}</a>{`.`}</p>
    <p>{`The following examples assume the modules were imported as such:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`from pandevice import firewall
from pandevice import network
`}</code></pre>
    <h2 {...{
      "id": "create-a-subinterface-and-commit"
    }}>{`Create a subinterface and commit:`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`fw = firewall.Firewall("10.0.0.1", api_username="admin", api_password="admin")
eth = fw.add(network.EthernetInterface("ethernet1/1", mode="layer3"))
subeth = eth.add(network.Layer3Subinterface("ethernet1/1.30", ip="4.4.4.4/24", tag=30))
subeth.create()
fw.commit()
`}</code></pre>
    <h2 {...{
      "id": "perform-show-system-info"
    }}>{`Perform `}<inlineCode parentName="h2">{`show system info`}</inlineCode></h2>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`fw = firewall.Firewall("10.0.0.1", api_username="admin", api_password="admin")
print fw.op("show system info")
`}</code></pre>
    <p>{`Some operational commands have methods to refresh the variables in an
object:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-python"
      }}>{`# populates the version, serial, and model variables from the live device
fw.refresh_system_info()
`}</code></pre>
    <blockquote>
      <p parentName="blockquote">{`See more examples in the `}<a parentName="p" {...{
          "href": "http://pandevice.readthedocs.io/en/latest/usage.html"
        }}>{`Usage Guide`}</a>{`.`}</p>
    </blockquote>
    <h2 {...{
      "id": "connecting-to-pan-os-80-and-higher"
    }}>{`Connecting to PAN-OS 8.0 and higher`}</h2>
    <p>{`PAN-OS 8.0 by default does not allow connections to the API with TLS
1.0. Unfortunately, the latest OSX and many linux distros come with
OpenSSL versions that don't support TLS 1.1 or 1.2. OpenSSL 1.0.1 or
higher is needed to connect to PAN-OS 8.0. If you try to connect with a
lower version of OpenSSL, you'll get a connection error. There are two
solutions:`}</p>
    <p><strong parentName="p">{`Option 1: Upgrade OpenSSL`}</strong>{` (more secure)`}</p>
    <p><em parentName="p">{`Mac OSX:`}</em>{` In Mac OSX you can't upgrade the built-in OpenSSL, but you
can install your own python and OpenSSL using `}<a parentName="p" {...{
        "href": "https://brew.sh/"
      }}>{`Homebrew`}</a>{`. Follow this
guide to get set up: `}<a parentName="p" {...{
        "href": "https://medium.com/@briantorresgil/definitive-guide-to-python-on-mac-osx-65acd8d969d0"
      }}>{`Definitive guide to python on OSX`}</a></p>
    <p><em parentName="p">{`Linux:`}</em>{` Use the instructions for your distribution's package manager to
upgrade OpenSSL to 1.0.1 or higher.`}</p>
    <p><strong parentName="p">{`Option 2: Enable TLS 1.0 on PAN-OS`}</strong>{` (less secure)`}</p>
    <p>{`Follow the direction in the PAN-OS Administrator Guide: `}<a parentName="p" {...{
        "href": "https://www.paloaltonetworks.com/documentation/80/pan-os/pan-os/certificate-management/replace-the-certificate-for-inbound-management-traffic"
      }}>{`Replace the
Certificate for Inbound Management Traffic`}</a></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
