  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'pan_guard_duty',
	title: 'PAN Guard Duty',
	sidebar_label: 'PAN Guard Duty'
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Support',
  id: 'support',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "integration-between-palo-alto-networks-vm-series-fw-and-amazon-guard-duty"
    }}>{`Integration between Palo Alto Networks VM-Series FW and Amazon Guard Duty`}</h1>
    <p>{`The following sections describe the steps to be able to consume Amazon Guard Duty
findings and subsequently apply security policy on the Palo Alto VM-Series Firewalls. `}</p>
    <h4 {...{
      "id": "pre-requisities"
    }}>{`Pre-Requisities`}</h4>
    <blockquote>
      <ul parentName="blockquote">
        <li parentName="ul">{`Requires the use of the pandevice python package`}</li>
        <li parentName="ul">{`Requires the use of the pan-python python package.`}</li>
      </ul>
    </blockquote>
    <p>{` `}<em parentName="p">{`Make sure to enable the Guard Duty service on your AWS account.`}</em></p>
    <h4 {...{
      "id": "step-by-step"
    }}>{`Step by Step`}</h4>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`Clone the repo`}</p>
        <blockquote parentName="li">
          <p parentName="blockquote">{`HTTPS: `}</p>
          <pre parentName="blockquote"><code parentName="pre" {...{
              "className": "language-console"
            }}>{`git clone https://github.com/PaloAltoNetworks/pan_guard_duty.git
`}</code></pre>
          <p parentName="blockquote">{`SSH: `}</p>
          <pre parentName="blockquote"><code parentName="pre" {...{
              "className": "language-console"
            }}>{`git clone git@github.com:PaloAltoNetworks/pan_guard_duty.git
`}</code></pre>
        </blockquote>
      </li>
      <li parentName="ol">
        <p parentName="li">{`In the same directory, install the pandevice and pan-python
packages as described below. `}</p>
        <blockquote parentName="li">
          <p parentName="blockquote">{`Make sure you are in pan_guard_duty's parent directory`}</p>
          <pre parentName="blockquote"><code parentName="pre" {...{
              "className": "language-console"
            }}>{`pip install pandevice -t <pan_guard_duty directory>
pip install pan-python -t <pan_guard_duty directory>
`}</code></pre>
        </blockquote>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Edit the "lambda_fw_config.py" file `}</p>
        <blockquote parentName="li">
          <p parentName="blockquote">{`Update the Mgmt IP of the VM-Series FW and the username and password
so that the lambda function can communicate with the FW. `}</p>
        </blockquote>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Create a zip file which contains the lambda code and the python packages. `}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-console"
          }}>{`cd <pan_guard_duty directory>
zip -r <filename>.zip .
`}</code></pre>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Create a bucket on S3`}</p>
        <blockquote parentName="li">
          <p parentName="blockquote">{`Create a folder named "lambda" in the bucket.`}</p>
        </blockquote>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Upload the zip file to the S3 bucket`}</p>
        <blockquote parentName="li">
          <p parentName="blockquote">{`Upload the zip file into the "lambda" folder
of the bucket.`}</p>
        </blockquote>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Make the changes in the Cloud Formation Template
to reference the name of the S3 bucket created in step 4. `}</p>
        <blockquote parentName="li">
          <p parentName="blockquote">{`Change the value of the "S3Bucket" key to be the name of the S3 bucket
created in step 4.
Change the value of the "S3Key" key to be "lambda/\\<name of the zip file uploaded`}{`>`}{`"`}</p>
        </blockquote>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Deploy the Cloud Formation Template. `}</p>
      </li>
    </ol>
    <h2 {...{
      "id": "support"
    }}>{`Support`}</h2>
    <p>{`This template/solution is released under an as-is, best effort, support policy. These scripts should be seen as community supported and Palo Alto Networks will contribute our expertise as and when possible. We do not provide technical support or help in using or troubleshooting the components of the project through our normal support options such as Palo Alto Networks support teams, or ASC (Authorized Support Centers) partners and backline support options. The underlying product used (the VM-Series firewall) by the scripts or templates are still supported, but the support is only for the product functionality and not for help in deploying or using the template or script itself. Unless explicitly tagged, all projects or work posted in our GitHub repository (at `}<a parentName="p" {...{
        "href": "https://github.com/PaloAltoNetworks"
      }}>{`https://github.com/PaloAltoNetworks`}</a>{`) or sites other than our official Downloads page on `}<a parentName="p" {...{
        "href": "https://support.paloaltonetworks.com"
      }}>{`https://support.paloaltonetworks.com`}</a>{` are provided under the best effort policy.`}</p>
    <p>{`For assistance from the community, please post your questions and comments either to the GitHub page where the solution is posted or on our Live Community site.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
